import React, { useEffect, useState } from "react";
import axios from "axios";

function NearbyTheaters() {
  const [movies, setMovies] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      () => {
        axios.get("https://api.themoviedb.org/3/movie/now_playing?api_key=YOUR_TMDB_API_KEY&language=en-US&page=1&")
          .then((res) => setMovies(res.data.results))
          .catch(() => setError("Could not fetch nearby movies."));
      },
      () => setError("Location access denied.")
    );
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2>Now Playing Movies Near You</h2>
      {error && <p>{error}</p>}
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {movies.map((movie) => (
          <div key={movie.id} style={{ margin: "10px", width: "200px" }}>
            <img
              src={`https://image.tmdb.org/t/p/w200${movie.poster_path}`}
              alt={movie.title}
              style={{ width: "100%" }}
            />
            <h4>{movie.title}</h4>
            <p>Release Date: {movie.release_date}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default NearbyTheaters;
