import React from "react";
import { Link } from "react-router-dom";

function Home() {
  return (
    <div style={{
      minHeight: "90vh",
      backgroundColor: "#121212",
      color: "#fff",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      padding: "30px",
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif"
    }}>

      <h1 style={{
        color: "yellow",
        fontSize: "4rem",
        letterSpacing: "5px",
        marginBottom: "10px"
      }}>
        IMDM
      </h1>

      <p style={{
        fontSize: "1.2rem",
        maxWidth: "600px",
        textAlign: "center",
        marginBottom: "30px",
        color: "#ccc"
      }}>
        Welcome to IMDM — your one-stop destination to search movies, explore trending TV shows, and see what’s playing near you.
      </p>

      <div style={{
        display: "flex",
        gap: "15px",
        flexWrap: "wrap"
      }}>
        <Link to="/movies">
          <button style={buttonStyle}>Search Movies</button>
        </Link>
        <Link to="/tvshows">
          <button style={buttonStyle}>TV Shows</button>
        </Link>
        <Link to="/nearby">
          <button style={buttonStyle}>Nearby Theaters</button>
        </Link>
      </div>
    </div>
  );
}

const buttonStyle = {
  padding: "12px 24px",
  fontSize: "1rem",
  borderRadius: "8px",
  border: "none",
  backgroundColor: "yellow",
  color: "#121212",
  fontWeight: "bold",
  cursor: "pointer",
  transition: "transform 0.2s ease",
};

export default Home;
